    private boolean isParenthesis(String p) {
        if (p.charAt(0) == ')') {
            return false;
        } else {
            Stack<Character> stack = new Stack<Character>();
            for (char ch: p.toCharArray()) {
                if (ch == '(') {
                    stack.push(ch);
                } else {
                    if (stack.isEmpty()) {
                        return false;
                    }
                    stack.pop();
                }
            }
            return stack.isEmpty();
        }
    }
