            return p;
        }
        int start = 0, end = 0, index = 0;
        // ë¬¸ìì—´ u, vë¡œ ë‚˜ëˆ„ê¸° index
        for (int i = 0; i < p.length(); i++) {
            if (p.charAt(i) == '(') {
                start++;
            } else {
                end++;
            }
            if (start == end) {
                index = i;
                break;
            }
        }
        StringBuilder u = new StringBuilder(p.substring(0, index + 1));
        StringBuilder v = new StringBuilder(p.substring(index + 1));

        // 3 : ì˜¬ë°”ë¥¸ ê´„í˜¸ í™•ì¸ -> ì˜¬ë°”ë¥´ë©´ ì˜¬ë°”ë¥¸ ë¬¸ìì—´(u)ì— vë¥¼ 1ë‹¨ê³„ë¶€í„° ë‹¤ì‹œí•˜ì—¬ ë¶™ì„
        if (isParenthesis(u.toString())) {
            u.append(split(v.toString()));
        } else { // 4 : ì˜¬ë°”ë¥¸ ê´„í˜¸ x -> ë¹ˆ ë¬¸ìì—´ì— '(' ì— ë¬¸ìì—´ vì— ëŒ€í•´ 1ë‹¨ê³„ë¶€í„° ë‹¤ì‹œí•˜ì—¬ ë¶™ì´ê³  ')'ë¥¼ ë‹¤ì‹œ ë¶™ì„
            // 4-1, 4-2, 4-3
            StringBuilder sb = new StringBuilder("(" + split(v.toString()) + ")");
            // 4-4 ì²«ë²ˆì§¸ì™€ ë§ˆì§€ë§‰ ë¶„ì ì œê±°í›„ ë‚˜ë¨¸ì§€ ë¬¸ìì—´ ê´„í˜¸ ë°©í–¥ ë°”ê¿ˆ
            u.deleteCharAt(u.length() - 1);
            u.deleteCharAt(0);
            u = new StringBuilder(reverse(u.toString()));
            return sb.append(u.toString()).toString();
        }
        return u.toString();
    }

    private boolean isParenthesis(String p) {
        if (p.charAt(0) == ')') {
            return false;
        } else {
            Stack<Character> stack = new Stack<Character>();
            for (char ch: p.toCharArray()) {
                if (ch == '(') {
                    stack.push(ch);
                } else {
                    if (stack.isEmpty()) {
                        return false;
                    }
                    stack.pop();
                }
            }
            return stack.isEmpty();
        }
    }

    private String reverse(String p) {
        StringBuilder sb = new StringBuilder();
        for (String s : p.split("")) {
            if (s.equals(")")) {
                sb.append("(");
            } else if (s.equals("(")){
                sb.append(")");
            }
        }
        return sb.toString();
    }
