```java
import java.util.Stack;

class Solution {
    public int solution(String s) {
        Stack<Character> stack = new Stack<Character>();
        for (char ch: s.toCharArray()) {
            if (stack.isEmpty()) {
                stack.push(ch);
            } else {
                // ë§ˆì§€ë§‰ì— ë“¤ì–´ì˜¨ ê°’ê³¼ í˜„ì¬ ë“¤ì–´ì˜¬ ê°’ì´ ê°™ìŒ
                if (stack.peek() == ch) {
                    stack.pop();
                } else {
                    stack.push(ch);
                }
            }
        }
        return stack.isEmpty() ? 1 : 0;
    }
}
